import React from 'react'
import '../css/style.css'
import Nav from 'react-bootstrap/Nav';

import 'bootstrap/dist/css/bootstrap.min.css';

const Nave = () => {
    //inspired from:
//https://css-tricks.com/snippets/css/typewriter-effect/
var TxtType = function(el, toRotate, period) {
    this.toRotate = toRotate;
    this.el = el;
    this.loopNum = 0;
    this.period = parseInt(period, 10) || 2000;
    this.txt = '';
    this.tick();
    this.isDeleting = false;
};

TxtType.prototype.tick = function() {
    var i = this.loopNum % this.toRotate.length;
    var fullTxt = this.toRotate[i];

    if (this.isDeleting) {
    this.txt = fullTxt.substring(0, this.txt.length - 1);
    } else {
    this.txt = fullTxt.substring(0, this.txt.length + 1);
    }

    this.el.innerHTML = '<span class="wrap">'+this.txt+'</span>';

    var that = this;
    var delta = 200 - Math.random() * 100;

    if (this.isDeleting) { delta /= 2; }

    if (!this.isDeleting && this.txt === fullTxt) {
    delta = this.period;
    this.isDeleting = true;
    } else if (this.isDeleting && this.txt === '') {
    this.isDeleting = false;
    this.loopNum++;
    delta = 500;
    }

    setTimeout(function() {
    that.tick();
    }, delta);
};

window.onload = function() {
    var elements = document.getElementsByClassName('typewrite');
    for (var i=0; i<elements.length; i++) {
        var toRotate = elements[i].getAttribute('data-type');
        var period = elements[i].getAttribute('data-period');
        if (toRotate) {
          new TxtType(elements[i], JSON.parse(toRotate), period);
        }
    }
    // INJECT CSS
    var css = document.createElement("style");
    css.type = "text/css";
    css.innerHTML = ".typewrite > .wrap { border-right: 0.06em solid black;}";
    document.body.appendChild(css);
};

    return (
        <div className='row'>
            <video autoPlay muted loop className='video col-1 col-s-1 '>
                <source src="./video/matrix1.mp4" type="video/mp4" />
                </video>
                <div className='cont'>
<img className='imgs' src="./images/profil.jpg" alt="" />
                <h1 className ='text-white'>Ramzi  <span>   Chabli</span></h1>
                <h2>Je Suis full stack developer</h2><h1>
  <a href="" class="typewrite" data-period="2000" data-type='[ "Ceci n&#39;est pas une pipe.", "RenÃ© Magritte"]'>
    <span class="wrap"></span>
  </a>
</h1>
                </div>

            <div className='content col-6'>


                <Nav defaultActiveKey="/home" className="flex-column oh">
                    <img className='imgt' src="./images/logo.webp" alt="" />
                    <Nav.Link className='yo' href="/home">Home</Nav.Link>
                    <Nav.Link className='yo' eventKey="link-1">About</Nav.Link>
                    <Nav.Link className='yo' eventKey="link-2">Services</Nav.Link>
                    <Nav.Link className='yo' eventKey="link-3">Portfolio</Nav.Link>
                    <Nav.Link className='yo' eventKey="link-4">Contact</Nav.Link>
                </Nav>
            </div>





        </div>

    )
}

export default Nave
